import React from "react";
import {
  Modal,
  Button,
  Descriptions,
  Image,
  Divider,
  Typography,
  Row,
  Col,
  Tag,
} from "antd";
import { formatDate } from "../../../utils/formatters";
import { getImageUrl } from "../../../utils/urlHelpers";
import { COUNTRIES } from "../../../constants/Countries.jsx";
import {
  CalendarOutlined,
  GlobalOutlined,
  FileTextOutlined,
} from "@ant-design/icons";

const { Title, Paragraph, Text } = Typography;

const ManufacturerDetailModal = ({ manufacturer, visible, onClose }) => {
  if (!manufacturer) return null; // Don't render if no manufacturer data

  const logoUrl = getImageUrl(manufacturer.logoUrl);

  // Get country name from country code
  const getCountryName = (countryCode) => {
    const country = COUNTRIES.find((c) => c.code === countryCode);
    return country ? country.name : countryCode;
  };

  const countryName = getCountryName(manufacturer.country);

  return (
    <Modal
      title={<Title level={4}>Manufacturer Details</Title>}
      open={visible}
      onCancel={onClose}
      width={800}
      footer={[
        <Button key="close" type="primary" onClick={onClose} size="large">
          Close
        </Button>,
      ]}
      centered
    >
      <Row gutter={[24, 24]}>
        <Col xs={24} sm={10} md={8} className="text-center">
          {logoUrl ? (
            <Image
              width="100%"
              src={logoUrl}
              alt={manufacturer.name || "Manufacturer"}
              className="mb-4"
              fallback="data:image/png;base64,"
            />
          ) : (
            <div className="bg-gray-100 p-8 rounded mb-4 flex items-center justify-center h-48">
              <Text type="secondary">No Logo Available</Text>
            </div>
          )}
          <div className="text-center">
            <Tag color="blue" icon={<GlobalOutlined />} className="mb-2">
              {countryName || manufacturer.country || "Unknown"}
            </Tag>
            {manufacturer.foundedYear && (
              <Tag color="green" icon={<CalendarOutlined />}>
                Founded {manufacturer.foundedYear}
              </Tag>
            )}
          </div>
        </Col>

        <Col xs={24} sm={14} md={16}>
          <Title level={3}>{manufacturer.name}</Title>

          {manufacturer.description ? (
            <>
              <Paragraph>
                <FileTextOutlined /> Description
              </Paragraph>
              <Paragraph className="bg-gray-50 p-3 rounded">
                {manufacturer.description}
              </Paragraph>
            </>
          ) : (
            <Paragraph type="secondary" italic>
              No description available
            </Paragraph>
          )}

          <Divider />

          <Descriptions column={{ xs: 1, sm: 2 }} size="small" bordered>
            <Descriptions.Item label="ID" span={2}>
              {manufacturer.id || "N/A"}
            </Descriptions.Item>
            <Descriptions.Item label="Created">
              {formatDate(manufacturer.createdDate) || "N/A"}
            </Descriptions.Item>
            <Descriptions.Item label="Created By">
              {manufacturer.createUser || "N/A"}
            </Descriptions.Item>
            <Descriptions.Item label="Last Modified">
              {formatDate(manufacturer.modifiedDate) || "N/A"}
            </Descriptions.Item>
            <Descriptions.Item label="Modified By">
              {manufacturer.modifiedUser || "N/A"}
            </Descriptions.Item>
          </Descriptions>
        </Col>
      </Row>
    </Modal>
  );
};

export default ManufacturerDetailModal;
